// ===================== LIVE PREVIEWS =====================

function SkillPreview({ value }) {
  const imgH = lerp3(value, 100, 72, 40);
  const mainFont = lerp3(value, 16, 14, 12);
  const stepOp = clamp(1 - (value - 0.15) / 0.4, 0, 1);
  const denseOp = clamp((value - 0.3) / 0.4, 0, 1);
  const recipeName = value < 0.35 ? "Smoked Hot Dogs" : value < 0.65 ? "Smoked Chicken Thighs" : "Competition Brisket";
  const recipeEmoji = value < 0.35 ? "🌭" : value < 0.65 ? "🍗" : "🥩";
  const grad = value < 0.35 ? "linear-gradient(135deg,#E8A050,#C07830)" : value < 0.65 ? "linear-gradient(135deg,#D4A060,#A07840)" : "linear-gradient(135deg,#5C2018,#3A1008)";

  return (
    <div className="rounded-xl overflow-hidden shadow-md bg-white border border-gray-100">
      <div className="relative overflow-hidden" style={{ height: imgH, background: grad, transition: "height 0.15s" }}>
        <div className="absolute inset-0 flex items-center justify-center text-4xl opacity-40">{recipeEmoji}</div>
        <div className="absolute bottom-0 left-0 right-0 p-3 bg-gradient-to-t from-black/60 to-transparent">
          <span className="text-white font-bold" style={{ fontSize: lerp3(value, 17, 15, 13) }}>{recipeName}</span>
        </div>
      </div>
      <div style={{ padding: lerp3(value, 16, 12, 10), fontSize: mainFont, transition: "padding 0.15s" }}>
        {value < 0.55 && (
          <div style={{ opacity: stepOp }}>
            <div className="flex items-center gap-2 mb-2">
              <span className="bg-green-100 text-green-700 text-xs font-semibold px-2 py-0.5 rounded-full">Step 1 of {value < 0.35 ? "3" : "6"}</span>
              <span className="text-xs text-gray-400">{value < 0.35 ? "5 min" : "15 min"}</span>
            </div>
            <p className="text-gray-600 leading-relaxed mb-3 text-sm">
              {value < 0.35
                ? "Place the hot dogs directly on the grill grates. That's it — you're doing great."
                : "Season the chicken thighs generously with your rub. Cover every surface, including under the skin."}
            </p>
            <div className="bg-green-500 text-white text-center py-2.5 rounded-lg font-semibold text-sm cursor-pointer hover:bg-green-600 transition-colors">
              Done — Next Step →
            </div>
          </div>
        )}
        {value > 0.3 && (
          <div style={{ opacity: denseOp }}>
            <div className="flex flex-wrap gap-1.5 mb-2">
              {(value > 0.65
                ? ["14 hrs","225°F","IT: 203°F","Post Oak","Wrap @ 165°F","Rest 2hrs","Fat-cap up"]
                : ["1.5 hrs","275°F","IT: 175°F","Cherry Wood","Skin-on"]
              ).map(t => <span key={t} className="text-[10px] px-2 py-0.5 rounded bg-gray-100 text-gray-600 font-medium">{t}</span>)}
            </div>
            <div className="text-[11px] text-gray-500 leading-relaxed">
              {value > 0.65 ? (
                <>
                  <strong className="text-gray-700">Injection:</strong> Beef broth, 1T Worcestershire, garlic powder. Inject on a grid pattern.
                  <br/><strong className="text-gray-700">Rub:</strong> 50/50 coarse pepper + kosher salt. 2T garlic, 1T onion.
                  <br/><strong className="text-gray-700">Method:</strong> Fat-cap up, spritz hourly post-bark. Wrap butcher paper @ 165°F internal. Pull at 203°F when probe-tender. Rest in cooler 2hrs minimum.
                </>
              ) : (
                <>
                  <strong className="text-gray-700">Rub:</strong> 2T paprika, 1T brown sugar, 1T garlic powder
                  <br/><strong className="text-gray-700">Method:</strong> Skin-on, 275°F until 175°F internal. Rest 10 min.
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

function AmbitionPreview({ value }) {
  const allRecipes = ["hotdog","burger","queso","wings","chicken","salmon","ribs","pork","brisket","wholehog"];
  const startIdx = Math.floor(lerp3(value, 0, 2, 5));
  const count = lerp3(value, 3, 4, 5);
  const visible = allRecipes.slice(startIdx, startIdx + Math.ceil(count));
  const highlightIdx = Math.floor(count / 2);

  return (
    <div className="space-y-2">
      <div className="flex items-center justify-between mb-1">
        <span className="text-xs font-semibold text-gray-500">
          {value < 0.33 ? "Quick Cooks" : value < 0.66 ? "Weekend Projects" : "Serious Smoke"}
        </span>
        <span className="text-[10px] text-gray-400">{visible.length} recipes</span>
      </div>
      {visible.map((food, i) => {
        const op = i < count ? 1 : clamp((count - i) * 3, 0, 1);
        return <div key={food} style={{ opacity: op }}><RecipeRow food={food} highlight={i === highlightIdx} /></div>;
      })}
    </div>
  );
}

function AccessibilityPreview({ value }) {
  const tempSize = lerp3(value, 28, 42, 68);
  const labelSize = lerp3(value, 10, 13, 18);
  const pad = lerp3(value, 12, 18, 28);
  const gap = lerp3(value, 6, 12, 20);
  const bg = lerp3Color(value, "#1A1A1A", "#FAFAFA", "#FFFFFF");
  const text = lerp3Color(value, "#E0E0E0", "#333333", "#111111");
  const sec = lerp3Color(value, "#888888", "#888888", "#444444");
  const multiOp = clamp(1 - (value - 0.2) / 0.3, 0, 1);

  return (
    <div className="rounded-xl overflow-hidden shadow-md" style={{ backgroundColor: bg, padding: pad, transition: "all 0.15s" }}>
      <div className="text-center" style={{ marginBottom: gap }}>
        <div className="font-bold" style={{ fontSize: tempSize, color: text, lineHeight: 1.1, transition: "font-size 0.15s" }}>225°F</div>
        <div style={{ fontSize: labelSize, color: sec, marginTop: 4, transition: "font-size 0.15s" }}>Target: 225°F</div>
      </div>
      <div className="text-center rounded-lg py-2" style={{ backgroundColor: "#4CAF5020", marginBottom: gap }}>
        <span className="font-semibold" style={{ fontSize: lerp3(value, 11, 14, 20), color: "#4CAF50" }}>
          {value > 0.7 ? "ON TRACK" : "On Track"}
        </span>
      </div>
      {value < 0.6 && (
        <div className="space-y-1.5" style={{ opacity: multiOp }}>
          {[
            { label: "Probe 1 (Flat)", temp: "168°F", target: "203°F", rate: "+4°/hr" },
            { label: "Probe 2 (Point)", temp: "172°F", target: "203°F", rate: "+5°/hr" },
            { label: "Ambient", temp: "227°F", target: "225°F", rate: "" },
            { label: "Pellet Level", temp: "62%", target: "", rate: "~4 hrs remain" },
          ].map(p => (
            <div key={p.label} className="flex justify-between" style={{ color: sec, fontSize: 10 }}>
              <span>{p.label}</span>
              <span>{p.temp}{p.target && ` / ${p.target}`} {p.rate && <span className="opacity-60">{p.rate}</span>}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

function VibePreview({ value }) {
  const bg = lerp3Color(value, "#1C1210", "#FFF8F0", "#FAFAFA");
  const cardBg = lerp3Color(value, "#2A1F1A", "#FFFFFF", "#FFFFFF");
  const text = lerp3Color(value, "#FFE0C0", "#5C4020", "#333333");
  const accent = lerp3Color(value, "#FF6B35", "#C49A6C", "#9E9E9E");
  const border = lerp3Color(value, "#3A2A1A", "#F0E0D0", "#EEEEEE");
  const fw = value < 0.33 ? 800 : value < 0.66 ? 500 : 300;
  const br = lerp3(value, 4, 12, 8);
  const shadow = value < 0.33 ? "0 2px 8px rgba(0,0,0,0.4)" : value < 0.66 ? "0 2px 12px rgba(0,0,0,0.08)" : "0 1px 4px rgba(0,0,0,0.06)";
  const headerName = value < 0.33 ? "THE PIT" : value < 0.66 ? "My Kitchen" : "traeger";
  const foodKey = value < 0.33 ? "ribs" : value < 0.66 ? "chicken" : "cauliflwr";
  const f = FOOD[foodKey];

  return (
    <div className="rounded-xl overflow-hidden" style={{ backgroundColor: bg, padding: 16, transition: "all 0.15s" }}>
      <div className="flex items-center justify-between mb-4">
        <span style={{ color: text, fontWeight: fw, fontSize: 16, letterSpacing: value < 0.33 ? "0.08em" : "0" }}>{headerName}</span>
        <div className="flex flex-col gap-1">
          {[100,70,40].map(w => <div key={w} style={{ height: 2, width: w + "%", minWidth: 8, maxWidth: 20, backgroundColor: accent, borderRadius: 1 }} />)}
        </div>
      </div>

      <div style={{ backgroundColor: cardBg, borderRadius: br, border: `1px solid ${border}`, boxShadow: shadow, overflow: "hidden", transition: "all 0.15s" }}>
        <div className="flex items-center" style={{ height: 48, background: f.gradient }}>
          <span className="text-2xl ml-3">{f.emoji}</span>
          <span className="text-white font-bold text-xs ml-2 drop-shadow">{f.name}</span>
        </div>
        <div style={{ padding: 12 }}>
          <div style={{ color: text, fontWeight: Math.min(fw, 600), fontSize: 13 }}>{f.name}</div>
          <div style={{ color: accent, fontSize: 11, fontWeight: 400 }}>{f.time}</div>
        </div>
      </div>

      <div className="mt-3 flex gap-2">
        <div className="flex-1 text-center" style={{ padding: "8px 0", borderRadius: br * 1.5, backgroundColor: accent, color: value < 0.33 ? "#1A0A00" : "#FFF", fontWeight: Math.min(fw, 600), fontSize: 12, letterSpacing: value < 0.33 ? "0.08em" : "0.02em", transition: "all 0.15s" }}>
          {value < 0.33 ? "FIRE IT UP" : value < 0.66 ? "Start Cooking" : "begin"}
        </div>
        <div style={{ padding: "8px 16px", borderRadius: br * 1.5, border: `1px solid ${accent}40`, color: accent, fontSize: 12, fontWeight: 400, transition: "all 0.15s" }}>
          {value < 0.33 ? "LOG" : value < 0.66 ? "Save" : "save"}
        </div>
      </div>
    </div>
  );
}

function ExplorationPreview({ value }) {
  const headerText = value < 0.33 ? "Your Favorites" : value < 0.66 ? "Suggested for You" : "New Discoveries";
  const items = [
    { food: "ribs",      sub: "Made 12 times",                    type: "fav" },
    { food: "chicken",   sub: "Made 8 times",                     type: "fav" },
    { food: "salmon",    sub: "Similar to your favorites",        type: "suggest" },
    { food: "macncheese",sub: "Crowd-pleaser for game day",       type: "suggest" },
    { food: "thaifish",  sub: "Trending this week",               type: "discover" },
    { food: "gochujang", sub: "Korean x Southern fusion",         type: "discover" },
    { food: "mole",      sub: "From Oaxacan tradition",           type: "discover" },
    { food: "asado",     sub: "Argentine wood-fire technique",    type: "discover" },
  ];
  const sliceEnd = lerp3(value, 2.2, 4.5, 8);

  return (
    <div>
      <div className="text-sm font-semibold text-gray-700 mb-2">{headerText}</div>
      <div className="space-y-1.5">
        {items.slice(0, Math.ceil(sliceEnd)).map((item, i) => {
          const op = i < sliceEnd ? 1 : clamp((sliceEnd - i) * 3, 0, 1);
          const isNew = item.type === "discover" || item.type === "suggest";
          const f = FOOD[item.food];
          return (
            <div key={item.food} className="flex items-center gap-2.5 p-2 rounded-lg" style={{
              opacity: op, backgroundColor: isNew && value > 0.3 ? "#FFF8E1" : "#F8F8F8",
              border: isNew && value > 0.3 ? "1px solid #FFE082" : "1px solid #F0F0F0",
            }}>
              <FoodThumb foodKey={item.food} emoji={f.emoji} gradient={f.gradient} size="sm" />
              <div className="flex-1 min-w-0">
                <div className="text-xs font-medium text-gray-800 truncate">{f.name}</div>
                <div className="text-[10px] text-gray-400">{item.sub}</div>
              </div>
              {isNew && value > 0.4 && (
                <span className="text-[9px] px-1.5 py-0.5 rounded-full font-medium shrink-0" style={{
                  backgroundColor: item.type === "suggest" ? "#E3F2FD" : "#E8F5E9",
                  color: item.type === "suggest" ? "#1565C0" : "#2E7D32",
                }}>{item.type === "suggest" ? "Try it" : "New"}</span>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

const PREVIEW_COMPONENTS = [SkillPreview, AmbitionPreview, AccessibilityPreview, VibePreview, ExplorationPreview];

// ===================== NUTRITION & SUMMARY =====================

function NutritionSelector({ selected, onChange }) {
  const toggle = (item) => {
    if (item === "No Preference") return onChange(["No Preference"]);
    const f = selected.filter(s => s !== "No Preference");
    onChange(f.includes(item) ? f.filter(s => s !== item) : [...f, item]);
  };
  return (
    <div className="flex flex-wrap gap-2 justify-center">
      {NUTRITION_OPTIONS.map(opt => {
        const a = selected.includes(opt);
        return (
          <button key={opt} onClick={() => toggle(opt)} className="px-4 py-2 rounded-full text-sm font-medium transition-all duration-150"
            style={{ backgroundColor: a ? "#5C4020" : "#F5F0EB", color: a ? "#FFF8F0" : "#5C4020", border: a ? "2px solid #5C4020" : "2px solid #E0D5C8" }}>
            {opt}
          </button>
        );
      })}
    </div>
  );
}

function ProfileSummary({ sliders, nutrition, isPersona, persona }) {
  const vv = isPersona ? persona.sliders[3] : sliders[3];
  const bg = lerp3Color(vv, "#2A1510", "#F5EDE4", "#FAFAFA");
  const text = lerp3Color(vv, "#FFE0C0", "#5C4020", "#333333");
  const accent = lerp3Color(vv, "#FF6B35", "#C49A6C", "#78909C");
  const cardBg = lerp3Color(vv, "#3A2518", "#FFFFFF", "#FFFFFF");
  const s = isPersona ? persona.sliders : sliders;
  const n = isPersona ? persona.nutrition : nutrition;

  return (
    <div className="rounded-2xl overflow-hidden shadow-xl max-w-sm mx-auto" style={{ backgroundColor: bg }}>
      <div className="p-6 flex flex-col items-center">
        {isPersona ? (
          <PersonaPortrait persona={persona} size={90} />
        ) : (
          <div className="w-20 h-20 rounded-full flex items-center justify-center text-2xl font-bold border-3 mb-1" style={{ borderColor: accent, color: accent, borderWidth: 3, backgroundColor: bg }}>You</div>
        )}
        <div className="font-bold text-lg mt-2" style={{ color: text }}>{isPersona ? persona.name : "Your Profile"}</div>
        {isPersona && <div className="text-sm opacity-70 mt-0.5" style={{ color: text }}>{persona.tagline}</div>}
        {isPersona && <p className="text-sm opacity-80 mt-2 leading-relaxed text-center" style={{ color: text }}>{persona.description}</p>}
      </div>
      <div className="px-6 py-4" style={{ backgroundColor: cardBg + "80" }}>
        {SLIDER_TITLES.map((title, i) => (
          <div key={i} className="mb-3 last:mb-0">
            <div className="flex justify-between text-[11px] mb-1">
              <span style={{ color: text, opacity: 0.7 }}>{title}</span>
              <span style={{ color: accent, fontWeight: 600, fontSize: 10 }}>
                {s[i] < 0.33 ? SLIDER_LABELS[i][0] : s[i] < 0.66 ? SLIDER_LABELS[i][1] : SLIDER_LABELS[i][2]}
              </span>
            </div>
            <div className="h-2 rounded-full overflow-hidden" style={{ backgroundColor: text + "15" }}>
              <div className="h-full rounded-full transition-all duration-500" style={{ width: `${s[i]*100}%`, background: SLIDER_GRADIENTS[i] }} />
            </div>
          </div>
        ))}
      </div>
      <div className="px-6 py-4 flex flex-wrap gap-1.5 justify-center">
        {n.map(item => <span key={item} className="text-xs px-3 py-1 rounded-full font-medium" style={{ backgroundColor: accent+"25", color: text }}>{item}</span>)}
      </div>
      {isPersona && (
        <div className="px-6 py-4 border-t" style={{ borderColor: text + "15" }}>
          <p className="text-sm italic text-center leading-relaxed" style={{ color: text, opacity: 0.75 }}>"{persona.hero}"</p>
        </div>
      )}
    </div>
  );
}

// ===================== MAIN APP =====================
export default function App() {
  const [screen, setScreen] = useState("select");
  const [sliders, setSliders] = useState([0.5, 0.5, 0.5, 0.5, 0.5]);
  const [nutrition, setNutrition] = useState([]);
  const [selectedPersona, setSelectedPersona] = useState(null);

  const nav = useCallback((target) => setScreen(target), []);

  const handlePersonaSelect = useCallback((p) => {
    setSelectedPersona(p); setSliders([...p.sliders]); setNutrition([...p.nutrition]); nav("persona-view");
  }, [nav]);

  const handleBuildOwn = useCallback(() => {
    setSelectedPersona(null); setSliders([0.5,0.5,0.5,0.5,0.5]); setNutrition([]); nav("slider-0");
  }, [nav]);

  const handleSliderChange = useCallback((i, v) => setSliders(p => { const n = [...p]; n[i] = v; return n; }), []);

  const si = screen.startsWith("slider-") ? parseInt(screen.split("-")[1]) : -1;
  const Preview = si >= 0 ? PREVIEW_COMPONENTS[si] : null;
  const totalSteps = 7;
  const curStep = si >= 0 ? si + 1 : screen === "nutrition" ? 6 : screen === "summary" ? 7 : 0;

  // ===== SCREENS =====

  if (screen === "select") {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 p-4 md:p-8">
        <div className="max-w-5xl mx-auto">
          <div className="text-center mb-8">
            <div className="text-sm font-medium text-amber-700 tracking-wider uppercase mb-2">GenUI Demo</div>
            <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-2">Choose Your Cook</h1>
            <p className="text-gray-500 max-w-lg mx-auto">Every cook is unique. Pick someone who feels familiar, or build your own profile from scratch.</p>
          </div>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-3 md:gap-4">
            {PERSONAS.map(p => <BaseballCard key={p.id} persona={p} onClick={() => handlePersonaSelect(p)} />)}
            <BuildYourOwnCard onClick={handleBuildOwn} />
          </div>
        </div>
      </div>
    );
  }

  if (screen === "persona-view") {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 flex flex-col items-center justify-center p-4">
        <div className="w-full max-w-sm">
          <ProfileSummary sliders={sliders} nutrition={nutrition} isPersona={true} persona={selectedPersona} />
          <div className="mt-6 space-y-3">
            <button onClick={() => nav("dashboard-preview")} className="w-full py-3.5 rounded-xl font-semibold text-white text-sm shadow-lg hover:shadow-xl transition-all" style={{ backgroundColor: selectedPersona.accent }}>
              Enter {selectedPersona.name}'s App →
            </button>
            <button onClick={() => nav("select")} className="w-full py-3 rounded-xl font-medium text-gray-500 text-sm bg-white border border-gray-200 hover:bg-gray-50">← Back to Selection</button>
          </div>
        </div>
      </div>
    );
  }

  if (screen === "dashboard-preview") {
    const p = selectedPersona || PERSONAS[0];
    const vv = (selectedPersona?.sliders || sliders)[3];
    const bg = lerp3Color(vv, "#1C1210", "#FFF8F0", "#FAFAFA");
    const text = lerp3Color(vv, "#FFE0C0", "#5C4020", "#333333");
    const accent = lerp3Color(vv, "#FF6B35", "#C49A6C", "#78909C");
    return (
      <div className="min-h-screen flex flex-col items-center justify-center p-4" style={{ backgroundColor: bg }}>
        <div className="text-center max-w-md">
          <div className="mb-4"><PersonaPortrait persona={p.skin ? p : {...p, skin:"#D4A574", hairColor:"#666", shirtColor:"#555"}} size={80} /></div>
          <h2 className="text-2xl font-bold mb-2" style={{ color: text }}>Welcome, {p.name}</h2>
          <p className="text-sm mb-8 opacity-70" style={{ color: text }}>Your app has been generated. Every screen — tailored to you.</p>
          <div className="text-sm opacity-50 italic" style={{ color: text }}>Personalized dashboards coming in the next build phase.</div>
          <button onClick={() => nav("select")} className="mt-8 px-8 py-3 rounded-xl font-medium text-sm" style={{ backgroundColor: accent+"20", color: accent, border: `1px solid ${accent}40` }}>
            ← Try Another Persona
          </button>
        </div>
      </div>
    );
  }

  if (si >= 0) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 flex flex-col">
        <div className="p-4 pb-0">
          <div className="flex gap-1 max-w-lg mx-auto">
            {Array.from({ length: totalSteps }).map((_, i) => <div key={i} className="flex-1 h-1 rounded-full" style={{ backgroundColor: i < curStep ? "#C49A6C" : "#E0D5C8" }} />)}
          </div>
        </div>
        <div className="flex-1 flex flex-col items-center justify-center p-4 md:p-8">
          <div className="w-full max-w-3xl">
            <div className="text-center mb-8">
              <div className="text-xs font-medium text-amber-700 tracking-wider uppercase mb-1">Step {curStep} of {totalSteps}</div>
              <h2 className="text-2xl md:text-3xl font-bold text-gray-800 mb-2">{SLIDER_TITLES[si]}</h2>
              <p className="text-gray-500">{SLIDER_DESCRIPTIONS[si]}</p>
            </div>
            <div className="grid md:grid-cols-2 gap-8 items-start">
              <div>
                <div className="bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
                  <GradientSlider value={sliders[si]} onChange={v => handleSliderChange(si, v)} labels={SLIDER_LABELS[si]} gradient={SLIDER_GRADIENTS[si]} />
                </div>
                <div className="mt-6 flex gap-3">
                  <button onClick={() => nav(si > 0 ? `slider-${si-1}` : "select")} className="flex-1 py-3 rounded-xl font-medium text-gray-500 text-sm bg-white border border-gray-200 hover:bg-gray-50">← Back</button>
                  <button onClick={() => nav(si < 4 ? `slider-${si+1}` : "nutrition")} className="flex-1 py-3 rounded-xl font-semibold text-white text-sm shadow-lg hover:shadow-xl" style={{ backgroundColor: "#5C4020" }}>Next →</button>
                </div>
              </div>
              <div>
                <div className="text-xs font-medium text-gray-400 uppercase tracking-wider mb-3 text-center">Live Preview</div>
                {Preview && <Preview value={sliders[si]} />}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (screen === "nutrition") {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 flex flex-col">
        <div className="p-4 pb-0">
          <div className="flex gap-1 max-w-lg mx-auto">
            {Array.from({ length: totalSteps }).map((_, i) => <div key={i} className="flex-1 h-1 rounded-full" style={{ backgroundColor: i < 6 ? "#C49A6C" : "#E0D5C8" }} />)}
          </div>
        </div>
        <div className="flex-1 flex flex-col items-center justify-center p-4 md:p-8">
          <div className="w-full max-w-md text-center">
            <div className="text-xs font-medium text-amber-700 tracking-wider uppercase mb-1">Step 6 of {totalSteps}</div>
            <h2 className="text-2xl md:text-3xl font-bold text-gray-800 mb-2">How Do You Eat?</h2>
            <p className="text-gray-500 mb-8">Select any that apply, or skip if you don't have a preference.</p>
            <NutritionSelector selected={nutrition} onChange={setNutrition} />
            <div className="mt-8 flex gap-3">
              <button onClick={() => nav("slider-4")} className="flex-1 py-3 rounded-xl font-medium text-gray-500 text-sm bg-white border border-gray-200 hover:bg-gray-50">← Back</button>
              <button onClick={() => nav("summary")} className="flex-1 py-3 rounded-xl font-semibold text-white text-sm shadow-lg" style={{ backgroundColor: "#5C4020" }}>See My Profile →</button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (screen === "summary") {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 flex flex-col">
        <div className="p-4 pb-0">
          <div className="flex gap-1 max-w-lg mx-auto">
            {Array.from({ length: totalSteps }).map((_, i) => <div key={i} className="flex-1 h-1 rounded-full" style={{ backgroundColor: "#C49A6C" }} />)}
          </div>
        </div>
        <div className="flex-1 flex flex-col items-center justify-center p-4 md:p-8">
          <div className="w-full max-w-sm">
            <div className="text-center mb-6">
              <h2 className="text-2xl font-bold text-gray-800 mb-1">Your Cook Profile</h2>
              <p className="text-gray-500 text-sm">Here's how we'll build your app.</p>
            </div>
            <ProfileSummary sliders={sliders} nutrition={nutrition.length > 0 ? nutrition : ["No Preference"]} isPersona={false} />
            <div className="mt-6 space-y-3">
              <button onClick={() => {
                setSelectedPersona({ id:"custom", name:"You", initials:"You", tagline:"Custom Profile", description:"", sliders, nutrition, accent: lerp3Color(sliders[3],"#FF6B35","#C49A6C","#78909C"), color: lerp3Color(sliders[3],"#2A1510","#F5EDE4","#FAFAFA"), textColor: lerp3Color(sliders[3],"#FFE0C0","#5C4020","#333"), skin:"#D4A574", hairColor:"#555", shirtColor:"#666", hero:"" });
                nav("dashboard-preview");
              }} className="w-full py-3.5 rounded-xl font-semibold text-white text-sm shadow-lg" style={{ backgroundColor: "#5C4020" }}>Start Cooking →</button>
              <button onClick={() => nav("nutrition")} className="w-full py-3 rounded-xl font-medium text-gray-500 text-sm bg-white border border-gray-200 hover:bg-gray-50">← Adjust Settings</button>
              <button onClick={() => nav("select")} className="w-full py-2 text-xs text-gray-400 hover:text-gray-600">Start Over</button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return null;
}
